mapper.update:
UPDATE COMM 
	SET LIKE_CNT = LIKE_CNT + 1 
	WHERE NO = #{no}

UPDATE COMM 
	SET HIT = HIT + 1 
	WHERE NO = #{no}

UPDATE COMM
	SET
	    TITLE = #{title}
	    ,CONTENT = #{content}
	    ,DELETE_YN = 'N'
	    ,MODIFY_DATE = SYSDATE
	WHERE
	NO = #{no}

UPDATE COMM 
SET DELETE_YN = 'Y' 
WHERE NO = #{no}
