mapper.update:
UPDATE COMM
	SET TITLE = #{title}
	    ,CONTENT = #{content}
	    ,DELETE_YN = 'N'
	    ,MODIFY_DATE = SYSDATE
	WHERE NO = #{no}

UPDATE COMM 
SET DELETE_YN = 'Y' 
WHERE NO = #{no}

mapper.select:
SELECT *
FROM COMM_ATTACHMENT
WHERE NO = #{no}

mapper.update:
UPDATE COMM_CMT 
SET DELETE_YN = 'Y' 
WHERE CMT_NO = #{cmtNo}

mapper.select:
UPDATE COMM 
SET REPORT_YN = 'Y' 
WHERE NO = #{no}
