mapper.select:
SELECT F.NO , QT.QNA_TYPE , F.TITLE , F.CONTENT , F.ENROLL_DATE , TO_CHAR(F.MODIFY_DATE, 'yyyy-mm-dd') AS MODIFY_DATE , F.MODIFY_DATE AS DATE2 FROM FAQ F JOIN QNA_TYPE QT ON F.QNA_TYPE_NO = QT.NO WHERE DELETE_YN = 'N'
AND QNA_TYPE_NO = ${typeNo}
ORDER BY DATE2 DESC

mapper.insert:
INSERT INTO FAQ
(
    NO
    , QNA_TYPE_NO
    , ADMIN_NO
    , TITLE
    , CONTENT
)
VALUES
(
    SEQ_FAQ_NO.NEXTVAL
    , #{qnaTypeNo} 
    , 1
    , #{title}
    , #{content}
)

 INSERT INTO QNA
 (
	 NO
	, MEMBER_NO
	, QNA_TYPE_NO
	, TITLE
	, CONTENT
)
VALUES
(
	SEQ_QNA_NO.NEXTVAL
	, 2
	, #{qnaTypeNo}
	, #{title}
    , #{content}
)

mapper.update:
	UPDATE QNA SET
    ANSWER = #{answer}
    , ANSWER_DATE = SYSDATE
    , ANSWER_YN = 'Y'
    WHERE NO = #{no}
