mapper.select:
SELECT P.PROD_NO, P.PROD_CATE_NO, P.PROD_NAME, P.PROD_PRICE, A.PROD_IMG_NO, A.THUMB_YN, A.CHANGE_NAME FROM YESSMALL_PROD P JOIN YESSMALL_ATTACHMENT A ON P.PROD_NO = A.PROD_NO JOIN YESSMALL_CATEGORY C ON P.PROD_CATE_NO = C.PROD_CATE_NO WHERE A.THUMB_YN = 'Y' AND A.STATUS = 'O' AND P.DELETE_YN = 'N'
AND P.PROD_CATE_NO = ${category}
AND P.PROD_NAME LIKE '%${search}%' ORDER BY PROD_NO DESC

mapper.update:
UPDATE 
	YESSMALL_PROD 
SET 
	PROD_NAME = #{prodName}
	, PROD_CATE_NO = #{prodCateNo}
	, PROD_INFO = #{prodInfo}
	, PROD_DETAIL = #{prodDetail}
	, PROD_PRICE = #{prodPrice}
	, PROD_STOCK = #{prodStock}
WHERE PROD_NO = #{prodNo}

mapper.select:
SELECT * FROM CART
WHERE PROD_NO = #{prodNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE 
	CART
SET 
	CNT = #{cnt }
WHERE PROD_NO = #{prodNo}
AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT * FROM YESS_LIKED
WHERE PROD_NO = #{prodNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
		DELETE FROM YESS_LIKED
     	WHERE PROD_NO = #{prodNo}
		AND MEMBER_NO = #{memberNo}
