mapper.select:
SELECT MK.NO , MB.NICK , MK.TITLE , MT.MARKET_TYPE , TO_CHAR(MK.ENROLL_DATE, 'yyyy-mm-dd') AS ENROLL_DATE , MK.HIT , MK.TRADE_YN , MA.CHANGE_NAME , COUNT(CASE WHEN MC.DELETE_YN = 'N' THEN 1 END) AS CMTCNT FROM MARKET MK JOIN MEMBER MB ON MK.MEMBER_NO = MB.NO JOIN MARKET_TYPE MT ON MK.MARKET_TYPE_NO = MT.NO JOIN MARKET_ATTACHMENT MA ON MK.NO = MA.MARKET_NO LEFT OUTER JOIN MARKET_CMT MC ON MK.NO = MC.MARKET_NO WHERE MK.DELETE_YN = 'N' AND MA.THUMB_YN = 'Y'
AND MARKET_TYPE_NO = ${tradeType}
AND TITLE LIKE '%${tradeName}%' GROUP BY MK.NO , MB.NICK , MK.TITLE , MT.MARKET_TYPE , TO_CHAR(MK.ENROLL_DATE, 'yyyy-mm-dd') , MK.HIT , MK.TRADE_YN , MA.CHANGE_NAME ORDER BY ENROLL_DATE DESC

SELECT COUNT(*) FROM MARKET WHERE DELETE_YN = 'N'
AND MARKET_TYPE_NO = ${tradeType}
AND TITLE LIKE '%${tradeName}%'

mapper.insert:
 	INSERT INTO MARKET
	(
	    NO
	    , MEMBER_NO
	    , TITLE
	    , MARKET_TYPE_NO
	    , OBJECT_NAME
	    , OBJECT_INFO
	    , TRADE_METHOD
	    , OBJECT_PS
	)
	VALUES
	(
	    SEQ_MARKET_NO.NEXTVAL
	    , 1
	    , #{title}
	    , #{marketType}
	    , #{objectName}
	    , #{objectInfo}
	    , #{tradeMethod}
	    , #{objectPs}
	)

INSERT INTO MARKET_ATTACHMENT
(
	NO
	, MARKET_NO
	, ORIGIN_NAME
	, CHANGE_NAME
	, FILE_PATH
	, THUMB_YN
)
VALUES
(
 	SEQ_MARKET_ATTACHMENT_NO.NEXTVAL 
 	, SEQ_MARKET_NO.CURRVAL
 	, #{originName}
	, #{changeName}
	, #{filePath}
	, #{thumbYn}
)

INSERT INTO MARKET_ATTACHMENT
(
	NO
	, MARKET_NO
	, ORIGIN_NAME
	, CHANGE_NAME
	, FILE_PATH
	, THUMB_YN
)
VALUES
(
 	SEQ_MARKET_ATTACHMENT_NO.NEXTVAL 
 	, #{marketNo}
 	, #{originName}
	, #{changeName}
	, #{filePath}
	, #{thumbYn}
)

mapper.update:
UPDATE MARKET 
SET HIT = HIT + 1 
WHERE NO = #{no}

UPDATE MARKET 
SET DELETE_YN = 'Y' 
WHERE NO = #{no}

UPDATE MARKET 
SET TRADE_YN = 'Y' 
WHERE NO = #{no}

UPDATE MARKET 
SET TRADE_YN = 'N' 
WHERE NO = #{no}

 	UPDATE MARKET SET
	    TITLE = #{title}
	    , MARKET_TYPE_NO = #{marketType}
	    , OBJECT_NAME = #{objectName}
	    , OBJECT_INFO = #{objectInfo}
	    , TRADE_METHOD = #{tradeMethod}
	    , OBJECT_PS = #{objectPs}
	    , MODIFY_DATE = SYSDATE
	WHERE NO = #{no}

mapper.insert:
INSERT INTO MARKET_CMT
