mapper.insert:
INSERT INTO MEMBER
(
	NO
	, ID
	, PWD
	, NAME
	, PHONE
	, NICK
	, ADDR
)
VALUES
(
	SEQ_MEMBER_NO.NEXTVAL 
	, #{id}
	, #{pwd}
	, #{name}
	, #{phone}
	, #{nick}
	, #{addr}
)

mapper.update:
UPDATE
    MEMBER
SET
    PHONE = #{phone}
    , NICK = #{nick}
    , ADDR = #{addr}
WHERE
    ID = #{id}
