mapper.insert:
INSERT INTO MEMBER
(
	NO
	, ID
	, PWD
	, NAME
	, NICK
	, PHONE
	, EMAIL
	, ADDR
)
VALUES
(
	SEQ_MEMBER_NO.NEXTVAL 
	, #{id}
	, #{pwd}
	, #{name}
	, #{nick}
	, #{phone}
	, #{email}
	, CONCAT( CONCAT( #{addr1}, #{addr2} ), #{addr3} )
)

mapper.update:
UPDATE
    MEMBER
SET
    PHONE = #{phone}
    , NICK = #{nick}
    , ADDR = #{addr}
WHERE
    ID = #{id}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE ID = #{id}

SELECT COUNT(*)
FROM MEMBER
WHERE NICK = #{nick}

SELECT COUNT(*)
FROM MEMBER
WHERE PHONE = ${phone}

SELECT COUNT(*)
FROM MEMBER
WHERE EMAIL = #{email}

mapper.update:
UPDATE
	MEMBER
SET
	PWD=#{pwd}
WHERE EMAIL = #{email}
