mapper.insert:
INSERT INTO MEMBER
(
	NO
	, ID
	, PWD
	, NAME
	, NICK
	, PHONE
	, EMAIL
	, ADDR1
	, ADDR2
	, ADDR3
)
VALUES
(
	SEQ_MEMBER_NO.NEXTVAL 
	, #{id}
	, #{pwd}
	, #{name}
	, #{nick}
	, #{phone}
	, #{email}
	, #{addr1}
	, #{addr2}
	, #{addr3}

)

mapper.update:
UPDATE
    MEMBER
SET
    PHONE = #{phone}
    , NICK = #{nick}
    , ADDR1 = #{addr1}
    , ADDR2 = #{addr2}
    , ADDR3 = #{addr3}
WHERE
    ID = #{id}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE ID = #{id}

SELECT COUNT(*)
FROM MEMBER
WHERE NICK = #{nick}

SELECT COUNT(*)
FROM MEMBER
WHERE EMAIL = #{email}

mapper.update:
UPDATE
	MEMBER
SET
	PWD=#{pwd}
WHERE EMAIL = #{email}

mapper.select:
SELECT ID
FROM MEMBER
WHERE EMAIL = #{email}
